require 'pry'

datastream = ''
START_OF_PACKET_LENGTH = 4

def packet_marker(input)
  anchor = 0
  runner = 3

  while runner < input.length do

    str_to_check = input.slice(anchor, START_OF_PACKET_LENGTH)
    has_dupes = str_to_check.chars.uniq.count { |str| str_to_check.count(str) > 1 }.positive?

    if has_dupes
      anchor += 1
      runner += 1
    else
      return runner + 1
    end
  end

end

packet_marker(datastream) # => 1929